import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
import "./App.css";

function App() {
const [user, setUser] = useState(null);
const [activeMenu, setActiveMenu] = useState("Dashboard");
const [devices, setDevices] = useState([]);

// Load devices sau khi login
useEffect(() => {
if (!user) return;

``
fetch(`http://localhost:5000/api/devices/user/${user.USER_ID}`)
  .then((res) => res.json())
  .then((data) => {
    console.log("DEVICE DATA:", data);
    setDevices(Array.isArray(data) ? data : []);
  })
  .catch((err) => console.error("Device load error:", err));


}, [user]);

// Logout
const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
setDevices([]);
};

// Add device mới
const addDevice = async (device) => {
try {
const res = await fetch("[http://localhost:5000/api/devices](http://localhost:5000/api/devices)", {
method: "POST",
headers: { "Content-Type": "application/json" },
body: JSON.stringify(device),
});
const newDevice = await res.json();
setDevices((prev) => [...prev, newDevice]);
} catch (err) {
console.error("Add device error:", err);
}
};

// Render content theo menu
let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={Array.isArray(devices) ? devices : []} />;
break;
case "Device":
content = ( <Device
       user={user}
       devices={devices}
       setDevices={setDevices}
       addDevice={addDevice}
     />
);
break;
default:
content = <Dashboard devices={Array.isArray(devices) ? devices : []} />;
}

// Nếu chưa login thì hiện login
if (!user) {
return <Login onLogin={setUser} />;
}

return ( <div className="app-container"> <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} /> <div className="main-content"> <Header user={user} onLogout={handleLogout} />
{content} </div> </div>
);
}

export default App;
