import React, { useState } from "react";
import Device from "./componentsDevice";
import Dashboard from "./components/Dashboard";

function App() {
  const [devices, setDevices] = useState([]); // state chung

  return (
    <div>
      {/* Truyền state và setState xuống Device để Device cập nhật */}
      <Device devices={devices} setDevices={setDevices} />

      {/* Truyền state devices xuống Dashboard */}
      <Dashboard devices={devices} />
    </div>
  );
}

export default App;
