import React, { useState, useEffect } from "react";

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: "Phòng khách",
    STATUS: "On",
  });

  const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        const url = `http://localhost:5000/api/devices-with-power/${user.USER_ID}`;
        console.log("Fetching URL:", url);

        const res = await fetch(url);
        console.log("Fetch response object:", res);

        if (!res.ok) {
          console.error("Server returned error:", res.status, res.statusText);
          return;
        }

        const data = await res.json();
        console.log("Fetched data:", data);

        const devicesWithColor = data.map((d) => ({
          ...d,
          color: "#" + Math.floor(Math.random() * 16777215).toString(16),
        }));

        setDevices(devicesWithColor);
      } catch (err) {
        console.error("Error fetching devices:", err);
      }
    };

    fetchDevices();
  }, [user, setDevices]);

  return <div>Check console for fetch logs</div>;
}

export default Device;
