import React, { useState, useEffect } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const DEFAULT_COLORS = ["#4caf50", "#2196f3", "#ff9800", "#9c27b0", "#f44336"];

function Dashboard({ user, devices, setDevices }) {
  const [visibleDevices, setVisibleDevices] = useState({});

  useEffect(() => {
    const mapVisible = {};
    devices.forEach((d) => (mapVisible[d.DEVICE_ID] = true));
    setVisibleDevices(mapVisible);
  }, [devices]);

  const handleToggleDevice = (id) => {
    setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  const filteredDevices = devices.filter((d) => visibleDevices[d.DEVICE_ID]);

  const totalKwh = filteredDevices.reduce(
    (sum, d) => sum + Number(d.kWh || 0),
    0
  );

  const chartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map(
          (d, idx) => d.color || DEFAULT_COLORS[idx % DEFAULT_COLORS.length]
        ),
      },
    ],
  };

  const lineChartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        borderColor: filteredDevices.map(
          (d, idx) => d.color || DEFAULT_COLORS[idx % DEFAULT_COLORS.length]
        ),
        borderWidth: 2,
        fill: false,
        tension: 0.4,
      },
    ],
  };

  const pieData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map(
          (d, idx) => d.color || DEFAULT_COLORS[idx % DEFAULT_COLORS.length]
        ),
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { legend: { display: false } },
    scales: { y: { beginAtZero: true } },
  };

  return (
    <div
      style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}
    >
      {" "}
      <h2>Dashboard</h2>
      {/* Toggle devices */}
      <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
        {devices.map((d) => (
          <button
            key={d.DEVICE_ID}
            onClick={() => handleToggleDevice(d.DEVICE_ID)}
            style={{
              padding: "6px 12px",
              borderRadius: 8,
              border: visibleDevices[d.DEVICE_ID]
                ? `2px solid ${d.color}`
                : "1px solid #ccc",
              backgroundColor: visibleDevices[d.DEVICE_ID] ? d.color : "#fff",
              color: visibleDevices[d.DEVICE_ID] ? "#fff" : "#000",
              cursor: "pointer",
            }}
          >
            {d.DEVICE_NAME}
          </button>
        ))}
      </div>
      {/* Total kWh */}
      <div
        style={{
          backgroundColor: "#fff",
          padding: 16,
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          minWidth: 200,
        }}
      >
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>
          {totalKwh} kWh
        </p>
      </div>
      {/* Charts */}
      <div style={{ display: "flex", gap: 24, flexWrap: "wrap" }}>
        <div
          style={{
            flex: "1 1 400px",
            backgroundColor: "#fff",
            padding: 16,
            borderRadius: 12,
          }}
        >
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div
          style={{
            flex: "1 1 400px",
            backgroundColor: "#fff",
            padding: 16,
            borderRadius: 12,
          }}
        >
          <h4>Biểu đồ đường</h4>
          <Line data={lineChartData} options={chartOptions} />
        </div>
        <div
          style={{
            flex: "1 1 300px",
            backgroundColor: "#fff",
            padding: 16,
            borderRadius: 12,
          }}
        >
          <h4>Biểu đồ tròn</h4>
          <Pie data={pieData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
