import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, onDevicesUpdate }) {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    kWh: "",
    ADDRESS: addresses[0],
    STATUS: "On",
    date: new Date().toISOString().slice(0, 10), // YYYY-MM-DD
  });

  // Load devices từ server
  useEffect(() => {
    fetchDevices();
  }, []);

  const fetchDevices = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/devices");
      const devicesWithColor = res.data.map((d) => ({
        ...d,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
      if (onDevicesUpdate) onDevicesUpdate(devicesWithColor);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  const handleAdd = async () => {
    if (!form.DEVICE_NAME || !form.kWh) return;

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        DEVICE_NAME: form.DEVICE_NAME,
        ADDRESS: form.ADDRESS,
        STATUS: form.STATUS,
        USER_ID: user.USER_ID,
        kWh: Number(form.kWh),
        time: form.date,
      });

      const newDevices = [
        ...devices,
        {
          ...res.data,
          color: "#" + Math.floor(Math.random() * 16777215).toString(16),
        },
      ];
      setDevices(newDevices);
      if (onDevicesUpdate) onDevicesUpdate(newDevices);

      setForm({
        DEVICE_NAME: "",
        kWh: "",
        ADDRESS: addresses[0],
        STATUS: "On",
        date: new Date().toISOString().slice(0, 10),
      });
    } catch (err) {
      console.error("Error adding device:", err);
      alert("Lưu thiết bị vào SQL thất bại!");
    }
  };

  return (
    <div>
      {/* Form thêm thiết bị - riêng hàng */}
      <div
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          marginBottom: "20px",
        }}
      >
        <h4>Thêm thiết bị mới</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ marginRight: "8px" }}
        />
        <input
          type="number"
          placeholder="kWh"
          value={form.kWh}
          onChange={(e) => setForm({ ...form, kWh: e.target.value })}
          style={{ marginRight: "8px" }}
        />
        <input
  type="date"
  value={form.date}
  onChange={(e) => setForm({ ...form, date: e.target.value })}
  style={{ width: "100%", marginBottom: "8px" }}
/>


        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ marginRight: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ marginRight: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd}>Lưu vào SQL</button>
      </div>

      {/* Device card hiển thị bên dưới */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              borderLeft: `4px solid ${d.color || "#000"}`,
              flex: "1 1 200px",
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>kWh: {d.kWh}</p>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>Ngày: {d.time}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
