import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
import Setting from "./components/Setting";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Load devices khi user thay đổi
  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        // ✅ sửa URL đúng với router server
        const res = await fetch(
          `http://localhost:5000/api/devices/with-power/${user.USER_ID}`
        );
        if (!res.ok) {
          console.warn(`Server responded with status: ${res.status}`);
          setDevices([]);
          return;
        }
        const data = await res.json();
        setDevices(
          data.map((d) => ({
            ...d,
            color: "#" + Math.floor(Math.random() * 16777215).toString(16),
            kWh: d.kWh ?? 0,
          }))
        );
      } catch (err) {
        console.error("Device load error:", err);
        setDevices([]);
      }
    };

    fetchDevices();
  }, [user]);

  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
    setDevices([]);
  };

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = (
        <Device user={user} devices={devices} setDevices={setDevices} />
      );
      break;
    case "Setting":
      content = <Setting user={user} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container">
      {" "}
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />{" "}
      <div className="main-content">
        {" "}
        <Header user={user} onLogout={handleLogout} />
        {content}{" "}
      </div>{" "}
    </div>
  );
}

export default App;
