import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
const [user, setUser] = useState(null);
const [activeMenu, setActiveMenu] = useState("Dashboard");
const [devices, setDevices] = useState([]);

// Logout
const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
setDevices([]);
};

// Nếu chưa login → hiện Login
if (!user) {
return <Login onLogin={setUser} />;
}

// Render nội dung theo menu
let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={devices} />;
break;
case "Device":
content = <Device user={user} onDevicesUpdate={setDevices} />;
break;
case "Setting":
content = <Setting user={user} />;
break;
default:
content = <Dashboard devices={devices} />;
}

return (
<div className="app-container" style={{ display: "flex", height: "100vh" }}> <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />

```
  <div className="main-content" style={{ flex: 1, padding: "16px", overflowY: "auto" }}>  
    <Header user={user} onLogout={handleLogout} />  
    {content}  
  </div>  
</div>  


);
}

export default App;
