import React from "react";
import { PieChart, Pie, Cell, Legend, Tooltip, ResponsiveContainer } from "recharts";

// Dữ liệu mock, có thể copy từ Device
const MOCK_DEVICES = [
  { DEVICE_ID: 1, DEVICE_NAME: "Máy lạnh", ADDRESS: "Phòng khách", STATUS: "On", kWh: 880 },
  { DEVICE_ID: 2, DEVICE_NAME: "Tivi", ADDRESS: "Phòng ngủ", STATUS: "Off", kWh: 110 },
  { DEVICE_ID: 3, DEVICE_NAME: "Quạt", ADDRESS: "Nhà bếp", STATUS: "On", kWh: 176 },
];

function Dashboard() {
  const devices = MOCK_DEVICES;

  const total = devices.length;
  const onCount = devices.filter((d) => d.STATUS === "On").length;
  const offCount = devices.filter((d) => d.STATUS === "Off").length;

  const data = [
    { name: "On", value: onCount },
    { name: "Off", value: offCount },
  ];

  const COLORS = ["#4caf50", "#f44336"];

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "24px" }}>
      <div style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px" }}>
        <h2>Dashboard</h2>
        <p>Tổng số thiết bị: {total}</p>
        <p>On: {onCount} / Off: {offCount}</p>
      </div>

      <div style={{ display: "flex", gap: "24px", flexWrap: "wrap" }}>
        <div style={{ flex: 1, minWidth: "300px", height: "300px", border: "1px solid #ccc", borderRadius: "8px", padding: "16px" }}>
          <h3>Trạng thái thiết bị</h3>
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={data}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={80}
                label
              >
                {data.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        <div style={{ flex: 2, minWidth: "300px", border: "1px solid #ccc", borderRadius: "8px", padding: "16px" }}>
          <h3>Danh sách thiết bị</h3>
          <ul>
            {devices.map((d) => (
              <li key={d.DEVICE_ID} style={{ marginBottom: "8px" }}>
                <strong>{d.DEVICE_NAME}</strong> - {d.ADDRESS} - {d.STATUS} - POWER: {d.kWh} W
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
