import React, { useMemo } from "react";
// ... (imports)

function Dashboard({ devices = [] }) {
  // ... (tính totalKwh và chartLabels)

  // Bọc logic tạo màu ngẫu nhiên/dữ liệu trong useMemo
  const memoizedPieData = useMemo(() => {
    const randomColors = devices.map(
        () => "#" + Math.floor(Math.random() * 16777215).toString(16).padStart(6, '0') // Đảm bảo đủ 6 ký tự
    );
    
    return {
      labels: chartLabels,
      datasets: [
        {
          data: devices.map((d) => Number(d.kWh || 0)),
          backgroundColor: randomColors,
          borderColor: "#fff",
          borderWidth: 1,
        },
      ],
    };
  }, [devices, chartLabels]); // Chỉ tính toán lại khi devices hoặc labels thay đổi

  // ...
  return (
    // ...
    <Pie data={memoizedPieData} options={options} />
    // ...
  );
}
