import React, { useState, useEffect, useCallback } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
Chart as ChartJS,
CategoryScale,
LinearScale,
BarElement,
LineElement,
PointElement,
ArcElement,
Tooltip,
Legend,
} from "chart.js";

ChartJS.register(
CategoryScale,
LinearScale,
BarElement,
LineElement,
PointElement,
ArcElement,
Tooltip,
Legend
);

const REFRESH_OPTIONS = [
{ label: "5 giây", value: 5000 },
{ label: "10 giây", value: 10000 },
{ label: "30 giây", value: 30000 },
{ label: "1 phút", value: 60000 },
{ label: "5 phút", value: 300000 },
{ label: "10 phút", value: 600000 },
{ label: "30 phút", value: 1800000 },
{ label: "1 giờ", value: 3600000 },
{ label: "3 giờ", value: 10800000 },
{ label: "5 giờ", value: 18000000 },
{ label: "10 giờ", value: 36000000 },
{ label: "24 giờ", value: 86400000 },
];

function Dashboard({ devices = [], fetchData }) {
const [visibleDevices, setVisibleDevices] = useState(
devices.reduce((acc, d) => ({ ...acc, [d.DEVICE_ID]: true }), {})
);
const [timeRange, setTimeRange] = useState("day");
const [customStart, setCustomStart] = useState("");
const [customEnd, setCustomEnd] = useState("");
const [filteredDevices, setFilteredDevices] = useState(devices);
const [refreshInterval, setRefreshInterval] = useState(10000);

const handleToggleDevice = (id) =>
setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
const handleTimeChange = (e) => setTimeRange(e.target.value);
const handleRefreshChange = (e) => setRefreshInterval(Number(e.target.value));

const filterDevices = useCallback(
(allDevices) => {
const now = new Date();
return allDevices.filter((d) => {
if (!visibleDevices[d.DEVICE_ID]) return false;
const deviceDate = new Date(d.timestamp || now);
if (timeRange === "day") return deviceDate.toDateString() === now.toDateString();
if (timeRange === "week") {
const weekStart = new Date(now);
weekStart.setDate(now.getDate() - now.getDay());
return deviceDate >= weekStart && deviceDate <= now;
}
if (timeRange === "month") {
return (
deviceDate.getMonth() === now.getMonth() &&
deviceDate.getFullYear() === now.getFullYear()
);
}
if (timeRange === "custom" && customStart && customEnd) {
const start = new Date(customStart);
const end = new Date(customEnd);
end.setHours(23, 59, 59, 999);
return deviceDate >= start && deviceDate <= end;
}
return true;
});
},
[visibleDevices, timeRange, customStart, customEnd]
);

useEffect(() => {
setFilteredDevices(filterDevices(devices));
}, [devices, filterDevices]);

useEffect(() => {
if (!fetchData) return;
const interval = setInterval(async () => {
const newDevices = await fetchData();
setFilteredDevices(filterDevices(newDevices));
}, refreshInterval);
return () => clearInterval(interval);
}, [fetchData, filterDevices, refreshInterval]);

const totalKwh = filteredDevices.reduce((sum, d) => sum + Number(d.kWh || 0), 0);

const chartData = {
labels: filteredDevices.map((d) => d.DEVICE_NAME),
datasets: [
{
label: "kWh",
data: filteredDevices.map((d) => Number(d.kWh || 0)),
backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderWidth: 2,
},
],
};

const lineChartData = {
labels: ["0", ...filteredDevices.map((d) => d.DEVICE_NAME)],
datasets: [
{
label: "kWh",
data: [0, ...filteredDevices.map((d) => Number(d.kWh || 0))],
borderColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderWidth: 3,
fill: false,
tension: 0.4,
},
],
};

const pieData = {
labels: filteredDevices.map((d) => d.DEVICE_NAME),
datasets: [
{
data: filteredDevices.map((d) => Number(d.kWh || 0)),
backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderColor: "#fff",
borderWidth: 4,
},
],
};

const chartOptions = {
responsive: true,
plugins: {
tooltip: {
callbacks: {
label: (context) => {
const d = filteredDevices[context.dataIndex];
if (!d) return "";
return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${d.STATUS || ""}`;
},
},
},
legend: { display: false },
},
scales: { y: { beginAtZero: true } },
};

return (
<div style={{ display: "flex", flexDirection: "column", gap: "24px", padding: "16px" }}>
{/* Filters */}
<div style={{ display: "flex", gap: "16px", flexWrap: "wrap", marginBottom: "12px" }}> <div> <label>Time Range:</label>
<select value={timeRange} onChange={handleTimeChange} style={{ marginLeft: "8px", padding: "4px 8px", borderRadius: "6px", border: "1px solid #ccc" }}> <option value="day">Hôm nay</option> <option value="week">Tuần này</option> <option value="month">Tháng này</option> <option value="custom">Tùy chỉnh</option> </select> </div>
{timeRange === "custom" && (
<div style={{ display: "flex", gap: "8px", alignItems: "center" }}> <label>From:</label>
<input type="date" value={customStart} onChange={(e) => setCustomStart(e.target.value)} /> <label>To:</label>
<input type="date" value={customEnd} onChange={(e) => setCustomEnd(e.target.value)} /> </div>
)} <div> <label>Refresh:</label>
<select value={refreshInterval} onChange={handleRefreshChange} style={{ marginLeft: "8px", padding: "4px 8px", borderRadius: "6px", border: "1px solid #ccc" }}>
{REFRESH_OPTIONS.map((o) => ( <option key={o.value} value={o.value}>{o.label}</option>
))} </select> </div> </div>

```
  {/* Device toggle buttons */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "12px", marginBottom: "12px" }}>
    {devices.map((d) => (
      <button
        key={d.DEVICE_ID}
        onClick={() => handleToggleDevice(d.DEVICE_ID)}
        style={{
          padding: "6px 12px",
          borderRadius: "8px",
          border: visibleDevices[d.DEVICE_ID] ? `2px solid ${d.color || "#4caf50"}` : "1px solid #ccc",
          backgroundColor: visibleDevices[d.DEVICE_ID] ? d.color || "#4caf50" : "#fff",
          color: visibleDevices[d.DEVICE_ID] ? "#fff" : "#000",
          cursor: "pointer",
          transition: "0.2s",
        }}
      >
        {d.DEVICE_NAME}
      </button>
    ))}
  </div>

  {/* Total energy */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
    <div style={{ backgroundColor: "#fff", padding: "16px", borderRadius: "12px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", minWidth: "200px" }}>
      <h4>Tổng năng lượng</h4>
      <p style={{ fontSize: "24px", fontWeight: "bold", margin: 0 }}>{totalKwh} kWh</p>
    </div>
    {filteredDevices.map((d) => (
      <div key={d.DEVICE_ID} style={{ backgroundColor: "#fff", padding: "12px", borderRadius: "12px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", minWidth: "180px" }}>
        <h5>{d.DEVICE_NAME}</h5>
        <p>kWh: {d.kWh}</p>
        <p>Trạng thái: {d.STATUS || "N/A"}</p>
        <p>Địa chỉ: {d.ADDRESS || "N/A"}</p>
      </div>
    ))}
  </div>

  {/* Charts */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "24px", marginTop: "16px" }}>
    <div style={{ flex: "1 1 400px", height: "300px", backgroundColor: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
      <h4>Biểu đồ cột</h4>
      <Bar data={chartData} options={chartOptions} />
    </div>
    <div style={{ flex: "1 1 400px", height: "300px", backgroundColor: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)" }}>
      <h4>Biểu đồ đường</h4>
      <Line data={lineChartData} options={chartOptions} />
    </div>
    <div style={{ flex: "1 1 300px", height: "300px", backgroundColor: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 12px rgba(0,0,0,0.1)", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center" }}>
      <h4>Biểu đồ tròn</h4>
      <div style={{ width: "80%", height: "80%" }}>
        <Pie data={pieData} options={{ ...chartOptions, maintainAspectRatio: true }} />
      </div>
    </div>
  </div>
</div>
``

);
}

export default Dashboard;
