import React, { useState, useEffect } from "react";
import axios from "axios";
import DeviceCard from "./DeviceCard";

// Khai báo bên ngoài component để tránh re-render
const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];
const initialFormState = {
  DEVICE_NAME: "",
  ADDRESS: addresses[0],
  STATUS: "On",
};

// Đã thêm isLoading state để cải thiện UX
function Device({ user, devices = [], setDevices }) {
  const [form, setForm] = useState(initialFormState);
  const [isLoading, setIsLoading] = useState(false);

  // Load devices từ server - Đã thêm setDevices vào dependencies để tuân thủ quy tắc Hook
  const fetchDevices = async () => {
    if (!user || !user.USER_ID) {
      setDevices([]);
      return;
    }
    try {
      setIsLoading(true);
      const res = await axios.get(
        // Đảm bảo user.USER_ID là hợp lệ (đã giải quyết lỗi 500 trước đó)
        `http://localhost:5000/api/devices/user/${user.USER_ID}` 
      );
      setDevices(res.data || []);
    } catch (err) {
      console.error("Error fetching devices:", err);
      // Hiển thị thông báo lỗi cho người dùng tốt hơn alert
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchDevices();
  }, [user, setDevices]); // Thêm setDevices vào dependency

  // Thêm device mới - Sử dụng try/catch cho feedback rõ ràng hơn
  const handleAdd = async () => {
    if (!form.DEVICE_NAME || !form.ADDRESS || !form.STATUS) {
      return alert("Vui lòng nhập đủ thông tin thiết bị!");
    }

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        USER_ID: user.USER_ID,
        ...form, // Dùng spread operator cho form
      });

      // Kiểm tra res.data để đảm bảo dữ liệu mới có DEVICE_ID
      if (res.data && res.data.DEVICE_ID) {
        setDevices([...devices, res.data]);
        setForm(initialFormState); // Reset form
      } else {
         throw new Error("Dữ liệu thiết bị mới không hợp lệ từ server.");
      }
    } catch (err) {
      console.error("Error saving device:", err);
      alert(`Lưu thiết bị thất bại! Chi tiết: ${err.message || "Lỗi không xác định."}`);
    }
  };

  // Xóa device
  const handleDelete = async (deviceId) => {
    if (!window.confirm(`Bạn có chắc chắn muốn xóa thiết bị ID ${deviceId} không?`)) {
        return; // Xác nhận trước khi xóa
    }
    try {
      await axios.delete(`http://localhost:5000/api/devices/${deviceId}`);
      setDevices(devices.filter((d) => d.DEVICE_ID !== deviceId));
    } catch (err) {
      console.error("Error deleting device:", err);
      alert("Xóa thiết bị thất bại!");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Quản lý Thiết Bị IoT</h2>
      <hr />
      
      {/* Form thêm thiết bị */}
      <div
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          maxWidth: "400px",
          marginBottom: "20px"
        }}
      >
        <h4>➕ Thêm thiết bị mới</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px", padding: "8px" }}
        />
        
        {/* Chọn Địa chỉ */}
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px", padding: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        
        {/* Chọn Trạng thái */}
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px", padding: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        
        <button 
            onClick={handleAdd} 
            style={{ width: "100%", padding: "10px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "4px", cursor: "pointer" }}
        >
          Thêm thiết bị
        </button>
      </div>
      
      <hr />

      {/* Danh sách device */}
      <h3>Danh Sách Thiết Bị Đã Thêm</h3>
      
      {/* Hiển thị Trạng thái */}
      {isLoading ? (
        <p>Đang tải danh sách thiết bị... ⏳</p>
      ) : (
        <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
          {devices.length > 0 ? (
             (devices || []).map((d) => (
                <DeviceCard
                    key={d.DEVICE_ID}
                    device={{
                        device_id: d.DEVICE_ID,
                        user_id: d.USER_ID,
                        name: d.DEVICE_NAME,
                        // Thêm kiểm tra d.kWh và d.CREATE_AT tránh lỗi undefined
                        kWh: d.kWh || 0, 
                        time: d.CREATE_AT || new Date().toISOString(), 
                        address: d.ADDRESS,
                        status: d.STATUS,
                    }}
                    onDelete={() => handleDelete(d.DEVICE_ID)}
                    onEdit={() => alert("Chức năng sửa sẽ thêm sau")}
                />
            ))
          ) : (
             // Kiểm tra nếu có user nhưng không có thiết bị
             user && <p>Chưa có thiết bị nào được đăng ký cho người dùng này.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Device;
