import React, { useState } from "react";
import Device from "./Device";
import Dashboard from "./Dashboard";

function App() {
  const [devices, setDevices] = useState([
    { id: 1, device_id: "D001", user_id: "U001", name: "Máy lạnh", kWh: 12, time: "10:00 - 12:00", address: "Phòng khách", status: "On", date: "2025-06-01" },
    { id: 2, device_id: "D002", user_id: "U002", name: "Tivi", kWh: 5, time: "18:00 - 20:00", address: "Phòng ngủ", status: "Off", date: "2025-06-02" },
  ]);

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-7xl mx-auto p-4">
        {/* Device quản lý thêm/sửa/xóa */}
        <Device devices={devices} setDevices={setDevices} />

        {/* Dashboard hiển thị biểu đồ */}
        <Dashboard devices={devices} />
      </div>
    </div>
  );
}

export default App;
